import React from 'react';
import { Route } from 'react-router-dom';
import { createStructuredSelector } from 'reselect';
import { connect } from 'react-redux';

import { fetchOffersStartAsync } from '../../redux/shop/shop.actions';
import {
  selectIsOfferFetching,
  selectIsOffersLoaded
} from '../../redux/shop/shop.selectors';

import WithSpinner from '../../components/with-spinner/WithSpinner';

import ShopOverviewContainer from '../../components/shop-overview/ShopOverviewContainer';
import Offers from '../offers/Offers';

const ShopOverviewWithSpinner = WithSpinner(ShopOverview);
const OffersWithSpinner = WithSpinner(Offers);

class Shop extends React.Component {
  componentDidMount() {
    const { fetchOffersStartAsync } = this.props;

    fetchOffersStartAsync();
  }
  render() {
    const { match, isOfferLoaded } = this.props;

    return (
      <div className='shop'>
        <Route exact path={`${match.path}`} component={ShopOverviewContainer} />
        <Route
          path={`${match.path}/:offersId`}
          render={props => (
            <OffersWithSpinner isLoading={!isOfferLoaded} {...props} />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  isOfferLoaded: selectIsOffersLoaded
});

const mapDispatchToProps = dispatch => ({
  fetchOffersStartAsync: () => dispatch(fetchOffersStartAsync())
});

export default connect(mapStateToProps, mapDispatchToProps)(Shop);
