import React from 'react';

export const FormInput = ({ handleChange, label, ...otherProps }) => {
  return (
    <div className='group'>
      <input
        type='text'
        onChange={handleChange}
        className='form-input'
        {...otherProps}
      />
      {label ? (
        <label
          className={`${
            otherProps.value.length ? 'shrink' : ''
          } form-input-label `}
        >
          {label}
        </label>
      ) : null}
    </div>
  );
};
