import React from 'react';
import { connect } from 'react-redux';

import ShopItem from '../shop-item/ShopItem';
import { selectOffer } from '../../redux/shop/shop.selectors';

import './shop-preview.scss';

const ShopPreview = ({ title, items, history, match, offers }) => {
  console.log(offers);
  return (
    <div className='shop-preview'>
      <h1
        className='title'
        onClick={() => {
          history.push(`${match.path}/${title.toLowerCase()}`);
        }}
      >
        {title.toUpperCase()}
      </h1>
      <div className='preview'>
        {items
          .filter((item, idx) => idx < 4)
          .map(item => (
            <ShopItem key={item.id} item={item} />
          ))}
      </div>
    </div>
  );
};

const mapStateToProps = (state, ownProps) => ({
  offers: selectOffer(ownProps.match.params.offersId)(state)
});

export default connect(mapStateToProps)(ShopPreview);
